mapper.update:
update online_exam.class_course
set classes_Id=#{new_ClassCourseInfoMapper.classes_Id},user_Id=#{new_ClassCourseInfoMapper.user_Id},cou_Id=#{new_ClassCourseInfoMapper.cou_Id}
where classes_Id=#{old_ClassCourseInfoMapper.classes_Id} and user_Id=#{old_ClassCourseInfoMapper.user_Id} and cou_Id=#{old_ClassCourseInfoMapper.cou_Id}

mapper.select:
select * from online_exam.view_teacher_class_info


        and classes_Id in

            #{item}



        and user_Id =#{user_Id}


        and cou_Id =#{cou_Id}



    LIMIT #{current},#{length};
